export const recordSkippedFire = async (
  scheduleId: number,
  customerId: number,
  plannedFor: Date,
  reason: string
) => {
  const knex = await getQueryBuilder();

  await knex
    .from("job_schedule_fires")
    .insert({
      scheduleId,
      customerId,
      plannedFor,
      status: 'SKIPPED',
      statusReason: reason,
      createdAt: new Date(),
    })
    .onConflict(['scheduleId', 'plannedFor'])
    .ignore();
};
