// NEW: Update legacy fire status
export const updateLegacyFireStatus = async (
  fireId: number,
  status: string,
  statusReason: string | null = null,
  durationMs: number | null = null
) => {
  const knex = await getQueryBuilder();

  const updateData: any = { status };

  if (statusReason) updateData.statusReason = statusReason;
  if (status === 'RUNNING') updateData.submittedAt = new Date();
  if (status === 'SUCCEEDED' || status === 'FAILED') {
    updateData.completedAt = new Date();
  }
  if (durationMs) updateData.durationMs = durationMs;

  await knex
    .from("jobScheduleFires")
    .update(updateData)
    .where("fireId", fireId);
};
