CREATE PROCEDURE [tableau].[GetLostSalesResultsByGroup]
AS
SET DATEFIRST 1;
DECLARE @RunID INT;

SELECT TOP 1 @RunID = ls.RunId
FROM lostsales.LostSalesRuns ls
		INNER JOIN engine.AlgoRuns ar ON ls.RunId = ar.RunId
WHERE ar.Status = 2
ORDER BY ls.RunId DESC; -- Gets the last completed LS Run.

DECLARE @StartDay DATE, @EndDay DATE, @LastLostSalesWeekNum INT,@LastLostSalesYearNum INT, @MaxWeekNumber INT, @MaxYearNumber INT;
SELECT @StartDay=DateFrom, @EndDay = DateTo FROM [lostsales].[LostSalesRuns]
WHERE RunID = @RunID;
SELECT @LastLostSalesYearNum = MAX(Year) FROM [lostsales].[LostSalesOutput]
WHERE RunID = @RunID;
SELECT @LastLostSalesWeekNum = MAX(Week) FROM [lostsales].[LostSalesOutput]
WHERE RunID = @RunID AND Year = @LastLostSalesYearNum;
SELECT @MaxYearNumber = COALESCE(MAX(Year),0) FROM [tableau].[LSResults];
SELECT @MaxWeekNumber = COALESCE(MAX(Week),0) FROM [tableau].[LSResults] WHERE Year = @MaxYearNumber;

ELECT @MaxWeekNumber = COALESCE(MAX(Week),0) FROM [tableau].[LSResults] WHERE Year = @MaxYearNumber;

DECLARE @IsThereNewRecords INT;
IF ((@MaxYearNumber IS NULL) OR(@LastLostSalesYearNum>@MaxYearNumber) OR (@LastLostSalesYearNum =@MaxYearNumber AND @LastLostSalesWeekNum>@MaxWeekNumber))
	SET @IsThereNewRecords = 1;
ELSE
    SET @IsThereNewRecords = 0;

N ls.RunId = ar.RunId
WHERE ar.Status = 2
ORDER BY ls.RunId DESC; -- Gets the last completed LS Run.

DECLARE @StartDay DATE, @EndDay DATE, @LastLostSalesWeekNum INT,@LastLostSalesYearNum INT, @MaxWeekNumber INT, @MaxYearNumber INT;
SELECT @StartDay=DateFrom, @EndDay = DateTo FROM [lostsales].[LostSalesRuns]
WHERE RunID = @RunID;
SELECT @LastLostSalesYearNum = MAX(Year) FROM [lostsales].[LostSalesOutput]
WHERE RunID = @RunID;
SELECT @LastLostSalesWeekNum = MAX(Week) FROM [lostsales].[LostSalesOutput]
WHERE RunID = @RunID AND Year = @LastLostSalesYearNum;

DECLARE @WarehouseLimit INT = 30;

SELECT @MaxYearNumber = COALESCE(MAX(Year),0) FROM [tableau].[LSResults];
SELECT @MaxWeekNumber = COALESCE(MAX(Week),0) FROM [tableau].[LSResults] WHERE Year = @MaxYearNumber;

ELECT @MaxWeekNumber = COALESCE(MAX(Week),0) FROM [tableau].[LSResults] WHERE Year = @MaxYearNumber;

DECLARE @IsThereNewRecords INT;
IF ((@MaxYearNumber IS NULL) OR(@LastLostSalesYearNum>@MaxYearNumber) OR (@LastLostSalesYearNum =@MaxYearNumber AND @LastLostSalesWeekNum>@MaxWeekNumber))
	SET @IsThereNewRecords = 1;
ELSE
    SET @IsThereNewRecords = 0;
